def on_on_overlap(sprite, other_sprite):
    global selected_option
    if other_sprite == fahrenheit_button and controller.A.is_pressed():
        selected_option = 1
        temperature_option_controller()
    elif other_sprite == celsius_button and controller.A.is_pressed():
        selected_option = 2
        temperature_option_controller()
sprites.on_overlap(SpriteKind.player, SpriteKind.OptionButton, on_on_overlap)

def convert_to_celsius(fahrenheit_temp: number):
    global converted_temp
    converted_temp = (fahrenheit_temp - 32) * (5 / 9)
    game.splash("Temperature in C = " + ("" + str(Math.round(converted_temp))))
def convert_to_fahrenheit(celsius_temp: number):
    global converted_temp
    converted_temp = celsius_temp * (9 / 5) + 32
    game.splash("Temperature in F = " + ("" + str(Math.round(converted_temp))))
